<template>
  <div class="info-card">
    <div class="info-card-header">
      <div class="info-card-header__title">{{ title }}</div>
      <div class="info-card-header__opt">
        <div class="filter-box" :class="keyword ? 'filter-box-actived' : ''">
          <span class="filter-text">{{ keyword }}</span>
          <span class="filter opt-icon" @click="openPrompt"></span>
        </div>
        <span class="clear-all opt-icon" @click="clearAll"></span>
        <span class="refresh opt-icon" @click="refresh"></span>
      </div>
    </div>
    <div class="info-card-body">
      <table v-show="Object.keys(filteredMap).length">
        <tbody>
          <tr class="" v-for="(value, key) in filteredMap" :key="key">
            <td class="info-key">{{ key }}</td>
            <td class="info-value">{{ value }}</td>
            <td class="info-opt">
              <span class="info-delete" @click="removeItem(key)"></span>
            </td>
          </tr>
        </tbody>
      </table>
      <div class="empty" v-show="Object.keys(filteredMap).length === 0">
        <span>empty</span>
      </div>
    </div>
  </div>
</template>
<script>
export default {
  props: {
    title: {
      default: "",
    },
    infoMap: {
      default: {},
    },
  },
  data() {
    return {
      keyword: "",
    };
  },
  computed: {
    filteredMap() {
      if (this.keyword) {
        let map = Object.create({})
        for (const key in this.infoMap) {
          if (Object.hasOwnProperty.call(this.infoMap, key)) {
            if(this.infoMap[key].indexOf(this.keyword) > -1 || key.indexOf(this.keyword) > -1) {
              map[key] = this.infoMap[key]
            }
          }
        }
        return map
      } else {
        return this.infoMap
      }
      
    }
  },
  methods: {
    removeItem(key) {
      this.$emit("removeItem", key);
    },
    openPrompt() {
      this.keyword = window.prompt(
        "请输入过滤关键词",
        this.keyword ? this.keyword : ""
      );
    },
    clearAll() {
      if (window.confirm(`将清空所有${this.title}数据，是否确认清空？`)) {
        this.$emit("clear");
      }
    },
    refresh() {
      console.log('refresh')
      this.$emit("refresh");
    },
  },
};
</script>
